import { useState, useEffect } from 'react';
import Movix from '../../assets/movix.png';
import './portfolio.css';

const Portfolio = (props) => {

    const { initialProjects } = props;
    const allProjects = initialProjects || [
        {
            id: 1,
            title: "Movix",
            description:
                "Provident similique accusantium nemo autem. Veritatis obcaecati tenetur iure eius earum ut molestias architecto.",
            imagesrc: { Movix },
            category: "Frontend",
            demoLink: "https://movix-eta.vercel.app/",
            githubLink: "https://github.com/aashumandal24/Movix-Aashu",
        },
        {
            id: 2,
            title: "Project Title One (UIUX)",
            description:
                "Provident similique accusantium nemo autem. Veritatis obcaecati tenetur iure eius earum ut molestias architecto.",
            image: "null",
            category: "UIUX",
            demoLink:
                "https://www.figma.com/proto/C8jaLhoyPVsEjcJiyi3RAY/Slider-Animation?page-id=0%3A1&node-id=9-232&p=f&viewport=478%2C79%2C0.1&t=M5Q5vZOYu3n4ffL2-1&scaling=min-zoom&content-scaling=fixed&starting-point-node-id=1%3A2",
            githubLink: "#portfolio",
        },
        {
            id: 3,
            title: "Project Title Three (Backend)",
            description:
                "Provident similique accusantium nemo autem. Veritatis obcaecati tenetur iure eius earum ut molestias architecto.",
            image: "null",
            category: "Frontend",
            demoLink: "#",
            githubLink: "#",
        },
        {
            id: 4,
            title: "Project Title Four (Frontend)",
            description:
                "Provident similique accusantium nemo autem. Veritatis obcaecati tenetur iure eius earum ut molestias architecto.",
            image: "null",
            category: "Frontend",
            demoLink: "#",
            githubLink: "#",
        },
        {
            id: 5,
            title: "Project Title Five (UIUX)",
            description:
                "Provident similique accusantium nemo autem. Veritatis obcaecati tenetur iure eius earum ut molestias architecto.",
            image: "null",
            category: "UIUX",
            demoLink: "#",
            githubLink: "#",
        },
        {
            id: 6,
            title: "Project Title Six (Backend)",
            description:
                "Provident similique accusantium nemo autem. Veritatis obcaecati tenetur iure eius earum ut molestias architecto.",
            image: "null",
            category: "UIUX",
            demoLink: "#",
            githubLink: "#",
        },
    ];

    const availableProjects = allProjects.filter(p => p.category !== 'Backend');
    const categories = ['All', 'Frontend', 'UIUX'];

    const [activeCategory, setActiveCategory] = useState('All');
    const [filteredProjects, setFilteredProjects] = useState(availableProjects);

    useEffect(() => {
        if (activeCategory === 'All') {
            setFilteredProjects(availableProjects);
        } else {
            const newFilteredProjects = availableProjects.filter(
                (project) => project.category === activeCategory
            );
            setFilteredProjects(newFilteredProjects);
        }
    }, [activeCategory]);

    return (
        <div id='portfolio' className="projects-section">
            <div className="container">
                <div className="header">
                    <h2 className="title">Recent Projects</h2>
                    <p className="subtitle">
                        Check out some of the projects I recently worked on for my clients. Use the buttons to toggle the different categories.
                    </p>
                </div>

                <div className="filter-buttons">
                    {categories.map((category) => (
                        <button
                            key={category}
                            className={`filter-btn ${activeCategory === category ? 'active' : ''}`}
                            onClick={() => setActiveCategory(category)}
                        >
                            {category}
                        </button>
                    ))}
                </div>

                <div className="projects-grid">
                    {filteredProjects.map((project) => (
                        <div key={project.id} className="project-card">
                            <div className="project-image">
                                <img src={project.image} alt={project.title} />
                            </div>
                            <div className="project-content">
                                <h3 className="project-title">{project.title}</h3>
                                <p className="project-description">{project.description}</p>
                                <div className="project-buttons">
                                    {/* <button className="white demo-btn">Demo</button> */}
                                    {/* <button className="primary github-btn">Github</button> */}
                                    <a href={project.demoLink} className='white demo-btn'>Demo</a>
                                    <a href={project.githubLink} className='primary github-btn'>Github</a>
                                </div>
                            </div>
                        </div>
                    ))}
                </div>
            </div>
        </div>
    );
};

export default Portfolio
