import { HiDownload } from 'react-icons/hi';
import AboutImage from '../../assets/about.webp';
import CV from '../../assets/resumee.pdf';
import Card from '../../components/Card';
import data from './data';
import './about.css';

const About = () => {
    return (
        <section id="about">
            <div className="container about_container">
                <div className="about_left">
                    <div className="about_portrait">
                        <img src={AboutImage} alt="about_image" />
                    </div>
                </div>
                <div className="about_right">
                    <h2>About Me</h2>
                    <div className="about_cards">
                        {
                            data.map(item => (
                                <Card key={item.id} className="about_card">
                                    <span className='about_card-icon'>{item.icon}</span>
                                    <h5>{item.title}</h5>
                                    <small>{item.desc}</small>
                                </Card>
                            ))
                        }
                    </div>
                    <p>
                        Building projects my clients love has always been my passion. Being in the web development industry for over 2 years and serving more than 70 happy clients worldwide, I'm always motivated to do more!
                    </p>
                    <p>
                        Hi, My name is Ayush Anand from Bihar, India. I'm a front-end web developer with a Bachelors degree in Computer Science. My top priority is to get your business online the right way, giving you industry-standard design and all the functionality you need to operate smoothly online. Get in touch today with the details of your project let's get started! Check out my resume below!
                    </p>
                    <a href={CV} download className='btn primary'>Download CV <HiDownload /></a>
                </div>
            </div>
        </section>
    )
}

export default About
