import React, { useState } from 'react';
import { Mail, Phone, MapPin, Send, Github, Linkedin, Twitter, User, MessageCircle } from 'lucide-react';
import './Contact.css';

interface FormData {
    name: string;
    email: string;
    message: string;
}

const Contact: React.FC = () => {
    const [formData, setFormData] = useState < FormData > ({
        name: '',
        email: '',
        message: ''
    });
    const [isSubmitting, setIsSubmitting] = useState(false);
    const [submitStatus, setSubmitStatus] = useState < 'idle' | 'success' | 'error' > ('idle');

    const handleInputChange = (e: React.ChangeEvent<HTMLInputElement | HTMLTextAreaElement>) => {
        const { name, value } = e.target;
        setFormData(prev => ({
            ...prev,
            [name]: value
        }));
    };

    const handleSubmit = async (e: React.FormEvent) => {
        e.preventDefault();
        setIsSubmitting(true);

        // Simulate form submission
        await new Promise(resolve => setTimeout(resolve, 1500));

        setSubmitStatus('success');
        setIsSubmitting(false);

        // Reset form after success
        setTimeout(() => {
            setFormData({ name: '', email: '', message: '' });
            setSubmitStatus('idle');
        }, 3000);
    };

    const contactInfo = [
        {
            icon: <Mail className="w-5 h-5" />,
            label: 'Email',
            value: 'hello@yourname.com',
            href: 'mailto:hello@yourname.com'
        },
        {
            icon: <Phone className="w-5 h-5" />,
            label: 'Phone',
            value: '+1 (555) 123-4567',
            href: 'tel:+15551234567'
        },
        {
            icon: <MapPin className="w-5 h-5" />,
            label: 'Location',
            value: 'San Francisco, CA',
            href: '#'
        }
    ];

    const socialLinks = [
        {
            icon: <Github className="w-5 h-5" />,
            label: 'GitHub',
            href: 'https://github.com',
            color: 'text-gray-700 hover:text-gray-900'
        },
        {
            icon: <Linkedin className="w-5 h-5" />,
            label: 'LinkedIn',
            href: 'https://linkedin.com',
            color: 'text-blue-600 hover:text-blue-700'
        },
        {
            icon: <Twitter className="w-5 h-5" />,
            label: 'Twitter',
            href: 'https://twitter.com',
            color: 'text-sky-500 hover:text-sky-600'
        }
    ];

    return (
        <section className="contact-section">
            <div className="contact-container">
                {/* Header */}
                <div className="contact-header">
                    <h2 className="contact-title">Get In Touch</h2>
                    <p className="contact-subtitle">
                        Ready to start your next project? Let's create something amazing together.
                    </p>
                </div>

                <div className="contact-content">
                    {/* Contact Form */}
                    <div className="contact-form-wrapper">
                        <div className="form-header">
                            <MessageCircle className="w-8 h-8 text-blue-600" />
                            <h3 className="form-title">Send Message</h3>
                        </div>

                        <form onSubmit={handleSubmit} className="contact-form">
                            <div className="form-group">
                                <label htmlFor="name" className="form-label">
                                    <User className="w-4 h-4" />
                                    Full Name
                                </label>
                                <input
                                    type="text"
                                    id="name"
                                    name="name"
                                    value={formData.name}
                                    onChange={handleInputChange}
                                    required
                                    className="form-input"
                                    placeholder="Enter your full name"
                                />
                            </div>

                            <div className="form-group">
                                <label htmlFor="email" className="form-label">
                                    <Mail className="w-4 h-4" />
                                    Email Address
                                </label>
                                <input
                                    type="email"
                                    id="email"
                                    name="email"
                                    value={formData.email}
                                    onChange={handleInputChange}
                                    required
                                    className="form-input"
                                    placeholder="Enter your email address"
                                />
                            </div>

                            <div className="form-group">
                                <label htmlFor="message" className="form-label">
                                    <MessageCircle className="w-4 h-4" />
                                    Message
                                </label>
                                <textarea
                                    id="message"
                                    name="message"
                                    value={formData.message}
                                    onChange={handleInputChange}
                                    required
                                    rows={4}
                                    className="form-textarea"
                                    placeholder="Tell me about your project..."
                                />
                            </div>

                            <button
                                type="submit"
                                disabled={isSubmitting}
                                className={`submit-button ${isSubmitting ? 'submitting' : ''} ${submitStatus === 'success' ? 'success' : ''}`}
                            >
                                {isSubmitting ? (
                                    <>
                                        <div className="spinner" />
                                        Sending...
                                    </>
                                ) : submitStatus === 'success' ? (
                                    <>
                                        <div className="checkmark">âœ“</div>
                                        Message Sent!
                                    </>
                                ) : (
                                    <>
                                        <Send className="w-4 h-4" />
                                        Send Message
                                    </>
                                )}
                            </button>
                        </form>
                    </div>

                    {/* Contact Info */}
                    <div className="contact-info-wrapper">
                        <div className="info-header">
                            <Mail className="w-8 h-8 text-teal-600" />
                            <h3 className="info-title">Contact Information</h3>
                        </div>

                        <div className="contact-info-list">
                            {contactInfo.map((item, index) => (
                                <a
                                    key={index}
                                    href={item.href}
                                    className="contact-info-item"
                                >
                                    <div className="info-icon">
                                        {item.icon}
                                    </div>
                                    <div className="info-content">
                                        <span className="info-label">{item.label}</span>
                                        <span className="info-value">{item.value}</span>
                                    </div>
                                </a>
                            ))}
                        </div>

                        <div className="social-section">
                            <h4 className="social-title">Follow Me</h4>
                            <div className="social-links">
                                {socialLinks.map((social, index) => (
                                    <a
                                        key={index}
                                        href={social.href}
                                        target="_blank"
                                        rel="noopener noreferrer"
                                        className={`social-link ${social.color}`}
                                        aria-label={social.label}
                                    >
                                        {social.icon}
                                    </a>
                                ))}
                            </div>
                        </div>

                        <div className="availability-badge">
                            <div className="status-indicator" />
                            <span>Available for new projects</span>
                        </div>
                    </div>
                </div>
            </div>
        </section>
    );
};

export default Contact;
